sp_widget.client_script:
api.controller=function($window, $scope) {
  /* widget controller */
  var c = this;

	$scope.openLink = function(id){
		//alert(id);
		//console.log("hello");
		$window.open("?sys_id="+id+"&view=sp&id=ticket&table=incident", "_self")
	}
};

sp_widget.script:
(function() {
	//create an array to populate with incidents
	data.incidents = [];
	var incidentGR = new GlideRecordSecure('incident');
	incidentGR.orderByDesc('sys_created_on');
	incidentGR.setLimit(5);
	incidentGR.query();
	while (incidentGR.next()) {
		var incidentObj = {};
		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(incidentObj, incidentGR, 'number,short_description,assignment_group,assigned_to,sys_id');
		//push the populated obj into the array
		data.incidents.push(incidentObj);
	}
})();
